mapper.select:
SELECT COUNT(member_id)
  FROM MEMBER
 WHERE member_email = #{email}

SELECT member_id AS memberId, member_name AS name, member_email AS email, member_pw AS password FROM MEMBER
WHERE member_email = #{email}
WHERE member_id = #{memberId}
WHERE email = '!@#$'

SELECT member_id
  FROM MEMBER
 WHERE member_email = #{email}

SELECT password
  FROM MEMBER
 WHERE member_id = #{memberId}
