mapper.update:
UPDATE SCHEDULE SET
	TITLE = #{title}, MEMO = #{memo},
	START_DT = #{startDt}, END_DT = #{endDt},
	IMPORTANT_YN = #{importantYn}
WHERE SCHEDULE_ID = #{scheduleId}
  AND MEMBER_ID = #{memberId}

mapper.delete:
DELETE
  FROM SCHEDULE
 WHERE SCHEDULE_ID = #{scheduleId}
   AND MEMBER_ID = #{memberId}

mapper.select:

	SELECT SCHEDULE_ID, TITLE, START_DT, END_DT, IMPORTANT_YN
	  FROM SCHEDULE
	 WHERE START_DT <= #{endDt} AND END_DT >= #{startDt}
	   AND MEMBER_ID = #{memberId}
	 ORDER BY START_DT

SELECT SCHEDULE_ID, TITLE, MEMO, START_DT, END_DT, IMPORTANT_YN
  FROM SCHEDULE
 WHERE SCHEDULE_ID = #{scheduleId}
   AND MEMBER_ID = #{memberId}


	SELECT COUNT(SCHEDULE_ID)
	  FROM SCHEDULE
	 WHERE START_DT <= #{endDt} AND END_DT >= #{startDt}
	   AND MEMBER_ID = #{memberId}
